SELECT a.*, b.name departname,c.name purchasetype,c.servicecode FROM t_depart_purchase_count a INNER JOIN t_depart b ON a.departid = b.id inner join t_purchase_type c on c.id = a.typeid WHERE 1 = 1
and a.departid = #{departid}
and a.typeid = #{type}
and a.typeid = #{typeid}
ORDER BY id DESC
limit #{offset}, #{limit}

