SELECT d.*,f.name departname,c.name purchasetype,c.servicecode FROM sys_user_role a INNER JOIN sys_role b ON a.role_id = b.role_id AND b.type = 2 AND b.rolesort = 1 INNER JOIN sys_user e ON e.user_id = a.user_id INNER JOIN t_depart f on f.id = e.departid INNER JOIN t_purchase_type c ON c.servicecode = b.roletype INNER JOIN t_depart_purchase_count d ON d.departid = e.departid AND d.typeid = c.id WHERE a.user_id = #{uid}
and e.departid = #{departid}
and d.typeid = #{type}
ORDER BY d.id DESC
limit #{offset}, #{limit}

SELECT count(*) FROM sys_user_role a INNER JOIN sys_role b ON a.role_id = b.role_id AND b.type = 2 AND b.rolesort = 1 INNER JOIN sys_user e ON e.user_id = a.user_id INNER JOIN t_depart f on f.id = e.departid INNER JOIN t_purchase_type c ON c.servicecode = b.roletype INNER JOIN t_depart_purchase_count d ON d.departid = e.departid AND d.typeid = c.id WHERE a.user_id = #{uid}
and e.departid = #{departid}
and d.typeid = #{type}

	SELECT
                a.typeid type,ifnull(d.remain,0) money, c.name typename,a.id
            FROM
                t_depart_purchase_count a 
            inner join t_purchase_type c on c.id = a.typeid
            left join t_user_purchase_limit d on d.purchasetypeid = c.id and d.uid = #{uid}
            WHERE
                1 = 1

            and a.departid = #{departid}


            and a.typeid = #{type}


            and a.typeid = #{typeid}


            and c.servicecode = #{servicecode}

            ORDER BY
                a.id DESC

            limit #{offset}, #{limit}
